function createStructure(tasks, groups, sections, stages) {
    groups.forEach(group => {
        group.children = tasks
            .filter(task => task.parentGroupId.toString() === group._id.toString())
            .map(task => {
                const childObject = Object.fromEntries(
                    Object.entries(task).map(([key, value]) => [
                        key,
                        Array.isArray(value) && value.length > 0 ? JSON.stringify(value) : value,
                    ])
                );
                return childObject;
            });
    });

    sections.forEach(section => {
        section.children = groups.filter(group => group.parentSectionId.toString() === section._id.toString());
    });

    stages.forEach(stage => {
        const groupforStage = groups.filter(group => group.parentStageId.toString() === stage._id.toString() && group.parentSectionId === "");
        const sectionforStage = sections.filter(section => section.parentStageId.toString() === stage._id.toString());

        stage.children = sectionforStage.length > 0 ? sectionforStage : groupforStage;
    });
}

createStructure(msg.tasks, msg.group, msg.section, msg.stages);
