function convertDateForGant(data) {
    const originalDate = new Date(data);

    // Ð¤ÑÐ½ÐºÑÑÑ Ð´Ð»Ñ Ð´Ð¾Ð´Ð°Ð²Ð°Ð½Ð½Ñ Ð²ÐµÐ´ÑÑÐ¾Ð³Ð¾ Ð½ÑÐ»Ñ Ð´Ð¾ ÑÐ¸ÑÐ»Ð°, ÑÐºÑÐ¾ Ð²Ð¾Ð½Ð¾ Ð¼ÐµÐ½ÑÐµ 10
    function addLeadingZero(num) {
        return num < 10 ? "0" + num : num;
    }

    // ÐÑÑÐ¸Ð¼Ð°Ð½Ð½Ñ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½ÑÑÐ² Ð´Ð°ÑÐ¸ ÑÐ° ÑÐ°ÑÑ
    const day = addLeadingZero(originalDate.getUTCDate());
    const month = addLeadingZero(originalDate.getUTCMonth() + 1);
    const year = originalDate.getUTCFullYear();

    // Ð¤Ð¾ÑÐ¼Ð°ÑÑÐ²Ð°Ð½Ð½Ñ ÑÐµÐ·ÑÐ»ÑÑÐ°ÑÑ
    const formattedDate = day + "." + month + "." + year;
    return formattedDate
}


