function convertDateForCompare(data) {
    const dateString = data;
    const dateMatch = dateString.match(/(\d{2})\.(\d{2})\.(\d{4})/);

    if (dateMatch) {
        // Ð¤Ð¾ÑÐ¼Ð°ÑÑÐ²Ð°Ð½Ð½Ñ Ð´Ð°ÑÐ¸: "DD.MM.YYYY"
        const formattedDate = `${dateMatch[3]}-${dateMatch[2]}-${dateMatch[1]}`;
        return new Date(formattedDate);
    }

    // Ð¯ÐºÑÐ¾ ÑÐ¾ÑÐ¼Ð°Ñ Ð´Ð°ÑÐ¸ Ð½Ðµ Ð²Ð´Ð°Ð»Ð¾ÑÑ ÑÐ¾Ð·Ð¿ÑÐ·Ð½Ð°ÑÐ¸
    return null;
}


function filterArr(arr, options) {
    return arr.filter(task => {
        return options.every(option => {
            const optionKey = Object.keys(option)[0];
            const optionValue = option[optionKey];            

            if (optionKey === "performer" && task.performer) {
                return task.performer == optionValue;
            }    
            if (optionKey === "startDate" && task.startDate) {
                const taskStartDate = convertDateForCompare(task.startDate);
                const optionDate = convertDateForCompare(optionValue);

                // ÐÐ¾ÑÑÐ²Ð½ÑÐ½Ð½Ñ Ð´Ð°Ñ, Ð½Ðµ Ð²ÑÐ°ÑÐ¾Ð²ÑÑÑÐ¸ ÑÐ°Ñ
                return taskStartDate && optionDate && taskStartDate.toISOString().split('T')[0] >= optionDate.toISOString().split('T')[0];
            }
            if (optionKey === "deadline" && task.deadline) {
                const taskEndDate = convertDateForCompare(task.deadline);
                const optionDate = convertDateForCompare(optionValue);

                // ÐÐ¾ÑÑÐ²Ð½ÑÐ½Ð½Ñ Ð´Ð°Ñ, Ð½Ðµ Ð²ÑÐ°ÑÐ¾Ð²ÑÑÑÐ¸ ÑÐ°Ñ
                return taskEndDate && optionDate && taskEndDate.toISOString().split('T')[0] <= optionDate.toISOString().split('T')[0];
            }

            return task[optionKey] === optionValue;
        });
    });
}


